import React from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme) => ({
  title: {
    fontSize: 36,
    color: '#0088D7',
    fontWeight: 500,
  },
  subtitle: {
    fontSize: 24,
  },
  methodName: {
    color: '#504F4F',
  },
  uploadButton: {
    width: 150,
    height: 40,  
  },
}));

export default function EduPoints() {
    const classes = useStyles();

    const methodList = [
      {
        "methodName": "Upload a document",
        "pointsNum": 50
      },
      {
        "methodName": "Add a new post",
        "pointsNum": 15
      },
      {
        "methodName": "Receive a comment",
        "pointsNum": 10
      },
      {
        "methodName": "Receive an upvote",
        "pointsNum": 10
      },
      {
        "methodName": "Create a new course",
        "pointsNum": 15
      },
      {
        "methodName": "Receive a download",
        "pointsNum": 15
      },
      {
        "methodName": "Refer a friend",
        "pointsNum": 100
      }
    ];
    const methodListComponent = methodList.map((name, i) => {
      return (
        <ListItem>
          <Grid container>
            <Grid item xs={2}>
              <ListItemIcon>

              </ListItemIcon>
            </Grid>
            <Grid item xs={5}>
              <ListItemText primary={methodList[i].methodName} className={classes.methodName}/>
            </Grid>
            <Grid item xs={1}>
              <ListItemText primary={methodList[i].pointsNum} className={classes.pointsNum}/>
            </Grid>
            <Grid item xs={2}>
              <ListItemText primary="EduPoints" className={classes.eduPointsText}/>    
            </Grid>
          </Grid>


        </ListItem>
      )
    });

    const premiumList = [
      "Unlimited access to all study materials",
      "Download as many documents as you like",
      "Cancel subscription anytime!"
    ];

    const premiumListComponent = premiumList.map((name, i) => {
      return (
        <ListItem>
          <Grid container>
            <Grid item xs={2}>
              <ListItemIcon>

              </ListItemIcon>
            </Grid>
            <Grid item xs={9}>
              <ListItemText>
                {premiumList[i]}
              </ListItemText>
            </Grid>
          </Grid>
        </ListItem>
      )
    });

    return (
      <div>
        <Grid 
          container 
          direction="column"
          alignItems="center">
          <Grid item>
            <Typography variant="h1" className={classes.title}> 
                How Do EduPoints Work?            
            </Typography>
          </Grid>
          <Grid item>
            <Typography variant="h3" className={classes.subtitle}>
            Earn EduPoints and Start Downloading Study Materials
            </Typography>
          </Grid>
        </Grid>

        <Grid container>
          <Grid item md={8}>

            <Grid
              container
              direction="row"
              alignItems="center"
              >
                <List>
                  {methodListComponent}
                </List>
                <Button variant="contained" disableElevation className={classes.uploadButton}>Upload</Button>
            </Grid>

          </Grid>
          <Grid item md={3}>
            {premiumListComponent}
          </Grid>
        </Grid>


      </div>
    );
}
